mapper.select:

  select * from (select rownum rnum, board.* from
(select * from board order by NUM desc) board)
where rnum >= #{first} and rnum <= #{second}


  select * from (select rownum rnum, board.* from
(select * from board order by views desc) board)
where rnum >= #{first} and rnum <= #{second}


  select * from (select rownum rnum, board.* from
(select * from board where category='자유' order by NUM desc) board)
where rnum >= #{first} and rnum <= #{second}


  select * from (select rownum rnum, board.* from
(select * from board where category='자유' order by views desc) board)
where rnum >= #{first} and rnum <= #{second}


  select * from (select rownum rnum, board.* from
(select * from board where category='유머' order by NUM desc) board)
where rnum >= #{first} and rnum <= #{second}


  select * from (select rownum rnum, board.* from
(select * from board where category='유머' order by views desc) board)
where rnum >= #{first} and rnum <= #{second}


  select * from (select rownum rnum, board.* from
(select * from board where writer=#{id} order by num desc) board)
where rnum >= #{first} and rnum <= #{second}
